import React, { useState, useEffect, useRef } from 'react';

function TodoForm(props) {
  const [input, setInput] = useState(props.edit ? props.edit.value : '');

  const inputRef = useRef(null);

  const [counter, setCounter] = useState(1);

  const [isInputValid, setIsInputValid] = useState(true);

  const [existingValues, setExistingValues] = useState([]);



  const incrementCounter = () => {
    setCounter(counter + 1);
  };

  useEffect(() => {
    inputRef.current.focus();
  });

  const handleChange = e => {
    setInput(e.target.value);
    setIsInputValid(true);
    setExistingValues([]);
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (input.trim() === '') {
      setIsInputValid(false);
    } else if (existingValues.includes(input)) {
      // Value is already present, handle duplicate value error
      console.log('Duplicate value entered');
    } else {
      props.onSubmit({
        id: counter,
        text: input
      });
      incrementCounter();
      setInput('');
      setIsInputValid(true);
      setExistingValues(prevValues => [...prevValues, input]);
    }
  };


  return (
    <form onSubmit={handleSubmit} className='todo-form'>
      {props.edit ? (
        <>
        <input
          placeholder='Add a todo'
          value={input}
          onChange={handleChange}
          name='text'
          className={`todo-input ${!isInputValid ? 'invalid' : ''}`}
          ref={inputRef}
        />
        { !isInputValid && <p className="error-message">Field cannot be empty</p> }
        { existingValues.includes(input) && <p className="error-message">Value already exists</p> }
        {/* ... */}
      </>
      ) : (
        <>
          <input
            placeholder='Add a todo'
            value={input}
            onChange={handleChange}
            name='text'
            className='todo-input'
            ref={inputRef}
          />
          <button onClick={handleSubmit} className='todo-button'>
            Add todo
          </button>
        </>
      )}
    </form>
  );
}

export default TodoForm;
