import { apiClient } from './ApiClient';
export const getAllTodos = apiClient.get(`/todos`);
export const deleteTodo = (username, id) => apiClient.delete(`/todos/${id}`);
export const retrieveTodo = (username, id) => apiClient.get(`/todos/${id}`);
export const updateTodo = (id, todo) => apiClient.put(`/todos/${id}`, todo);
export const createTodo = todo => apiClient.post(`/todos`, todo);
