import React, { useState, useEffect } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo';

import { apiClient } from './ApiClient'

function TodoList() {

    const [todos, setTodos] = useState([]);

    useEffect(() => {
      const fetchTodos = async () => {
        try {
          const response = await apiClient.get(`/todos`);
          const transformedTodos = response.data.map((todo) => ({
            id: `${todo.id}`, // Prefix 'react_' to the API item ID
            name: todo.name,
          }));
          setTodos(transformedTodos);
        } catch (error) {
          console.error('Error retrieving Todos:', error);
        }
      };
  
      fetchTodos();
    }, []);

    const [todo, setTodo] = useState(null);

    useEffect(() => {
      const fetchTodo = async () => {
        try {
          const response = await apiClient.get(`/todos/${id}`);
          setTodo(response.data);
        } catch (error) {
          console.error('Error retrieving todo:', error);
        }
      };

      fetchTodo();
    }, [todoId]);
    
      




    
  
    const addTodo = (todo) => {
      apiClient.post(`/todos`, todo)
      if (!todo.text || /^\s*$/.test(todo.text)) {
        return;
      }
  
      const newTodos = [todo, ...todos];
  
      setTodos(newTodos);
      console.log(...todos);
    }

    const updateTodo = (id, todo) => {
      apiClient.put(`/todos/${id}`, todo)
      if (!todo.text || /^\s*$/.test(todo.text)) {
        return;
      }
  
      setTodos(prev => prev.map(item => (item.id === id ? todo : item)));
    };
  
    const removeTodo = (id) => {
      apiClient.delete(`/todos/${id}`)
      const removedArr = [...todos].filter(todo => todo.id !== id);
      setTodos(removedArr);
    }



    const completeTodo = (id) => {
        let updatedTodos = todos.map(todo => {
          if (todo.id === id) {
            todo.isComplete = !todo.isComplete;
          }
          return todo;
        });
        setTodos(updatedTodos);
      };

      


      return (
        <>
          <h1>What's the Plan for Today?</h1>
          <TodoForm onSubmit={addTodo} />
          <Todo
            todos={todos}
            completeTodo={completeTodo}
            removeTodo={removeTodo}
            updateTodo={updateTodo}
          />
        </>
      );
}
export default TodoList;
